'use client';

import { AnimatePresence, motion } from "motion/react";
import { useState } from "react";

import { FloatingLogo } from "@/components/sidebar/floating-logo";
import { FloatingTabBars } from "@/components/sidebar/floating-tab-bars";
import { SidebarFooter } from "@/components/sidebar/sidebar-footer";
import { SidebarHeader } from "@/components/sidebar/sidebar-header";
import { SidebarNav } from "@/components/sidebar/sidebar-nav";
import { DEFAULT_ACTIVE_ITEM } from "@/lib/sidebar/navigation";
import { NavigationItemId } from "@/lib/sidebar/navigation";

export function Sidebar() {
  const [activeItem, setActiveItem] = useState<NavigationItemId>(DEFAULT_ACTIVE_ITEM);
  const [isCollapsed, setIsCollapsed] = useState(false);

  const handleMenuClick = () => {
    setIsCollapsed(!isCollapsed);
  };

  return (
    <AnimatePresence mode="wait" initial={false}>
      {isCollapsed ? (
        <>
          <FloatingLogo key="logo" />
          <FloatingTabBars
            key="toolbar"
            activeItem={activeItem}
            onItemClick={setActiveItem}
            onMenuClick={handleMenuClick}
          />
        </>
      ) : (
        <motion.div
          key="sidebar"
          className="box-border content-stretch flex flex-col gap-[4px] h-full items-start pl-[12px] pr-[8px] py-[12px] relative shrink-0"
          initial={false}
          animate={{
            width: 255,
            opacity: 1,
          }}
          exit={{
            width: 255,
            opacity: 0,
            transition: {
              duration: 0.25,
              ease: [0.32, 0.72, 0, 1],
            },
          }}
          transition={{
            type: "spring",
            stiffness: 400,
            damping: 30,
            mass: 0.8,
          }}
          data-testid="sidebar"
          data-collapsed={isCollapsed}
          layout
        >
          <motion.div
            className="backdrop-blur-[12.7px] backdrop-filter basis-0 box-border content-stretch flex flex-col grow items-start min-h-px min-w-px pb-[12px] pt-0 px-0 relative rounded-[24px] shrink-0 w-full transition-colors duration-500"
            style={{
              backgroundColor: "rgba(255, 255, 255, 0.05)",
            }}
            layout
          >
            <div className="basis-0 grow min-h-px min-w-px relative shrink-0 w-full">
              <div className="size-full">
                <div className="box-border content-stretch flex flex-col items-start justify-between pl-0 pr-px py-0 relative size-full">
                  <motion.div
                    className="content-stretch flex flex-col gap-[16px] items-start relative shrink-0 w-full"
                    exit={{
                      opacity: 0,
                      y: -10,
                      transition: { duration: 0.2 },
                    }}
                  >
                    <SidebarHeader onMenuClick={handleMenuClick} isCollapsed={false} />
                    <SidebarNav
                      activeItem={activeItem}
                      onItemClick={setActiveItem}
                      isCollapsed={false}
                    />
                  </motion.div>
                  <motion.div
                    exit={{
                      opacity: 0,
                      y: 10,
                      transition: { duration: 0.2 },
                    }}
                  >
                    <SidebarFooter
                      activeItem={activeItem}
                      onItemClick={setActiveItem}
                      isCollapsed={false}
                    />
                  </motion.div>
                </div>
              </div>
            </div>
          </motion.div>
        </motion.div>
      )}
    </AnimatePresence>
  );
}

"use client"

import { AnimatePresence, motion } from "motion/react"
import { useMemo } from "react"

import { FloatingLogo } from "@/components/sidebar/floating-logo"
import { SidebarContent } from "@/components/sidebar/sidebar-content"
import { TabBarsContent } from "@/components/sidebar/tab-bars-content"
import { useTheme } from "@/components/theme/theme-provider"
import { MORPH_SCALE_VALUES, MORPH_TIMING, MORPH_TRANSITION } from "@/lib/sidebar/morph"
import { useNavigationMorph } from "@/lib/sidebar/hooks/use-navigation-morph"
import { useNavigationState } from "@/lib/sidebar/hooks/use-navigation-state"
import { getShadow } from "@/lib/utils/animations"

export function NavigationContainer() {
  const { activeItem, setActiveItem } = useNavigationState()
  const { isCollapsed, showTabBarsContent, toggleCollapse } = useNavigationMorph(MORPH_TIMING.contentSwitchDelay)
  const { theme } = useTheme()
  const isDark = theme === "dark"

  const shadowValue = useMemo(() => getShadow(isDark, isCollapsed), [isDark, isCollapsed])

  const containerStyle = useMemo(
    () => ({
      zIndex: isCollapsed ? 50 : "auto",
      willChange: isCollapsed ? "transform, width, height, opacity" : "auto",
      backfaceVisibility: "hidden" as const,
      WebkitFontSmoothing: "antialiased" as const,
    }),
    [isCollapsed],
  )

  return (
    <>
      <AnimatePresence>{isCollapsed && <FloatingLogo key="logo" />}</AnimatePresence>

      <motion.div
        className="shrink-0"
        layoutId="navigation-container"
        initial={false}
        animate={{
          position: isCollapsed ? "fixed" : "relative",
          top: isCollapsed ? 12 : undefined,
          left: isCollapsed ? "50%" : undefined,
          x: isCollapsed ? "-50%" : 0,
          width: isCollapsed ? "auto" : 255,
          height: isCollapsed ? 48 : "100%",
          paddingLeft: isCollapsed ? 0 : 12,
          paddingRight: isCollapsed ? 0 : 8,
          paddingTop: isCollapsed ? 0 : 12,
          paddingBottom: isCollapsed ? 0 : 12,
        }}
        transition={MORPH_TRANSITION.container}
        style={containerStyle}
        data-testid={isCollapsed ? "toolbar" : "sidebar"}
        data-collapsed={isCollapsed}
      >
        <motion.div
          layoutId="navigation-backdrop"
          className="backdrop-blur-[12px] backdrop-filter box-border content-stretch flex items-start relative transition-colors duration-500"
          animate={{
            height: isCollapsed ? 48 : "100%",
            width: isCollapsed ? "auto" : "100%",
            borderRadius: 24,
            paddingLeft: isCollapsed ? 12 : 0,
            paddingRight: isCollapsed ? 12 : 0,
            paddingTop: isCollapsed ? 6 : 0,
            paddingBottom: isCollapsed ? 6 : 12,
            flexDirection: isCollapsed ? "row" : "column",
            gap: isCollapsed ? 6 : 0,
            scale: isCollapsed ? MORPH_SCALE_VALUES.collapse : MORPH_SCALE_VALUES.expand,
          }}
          transition={{ ...MORPH_TRANSITION.container, scale: MORPH_TRANSITION.scale }}
          style={{
            backgroundColor: "rgba(255, 255, 255, 0.05)",
            overflow: "hidden",
            willChange: isCollapsed ? "transform, opacity, width, height" : "auto",
            backfaceVisibility: "hidden",
            perspective: 1000,
            transform: "translateZ(0)",
          }}
        >
          <motion.div
            className="absolute inset-0 pointer-events-none rounded-[24px]"
            animate={{ boxShadow: shadowValue }}
            transition={MORPH_TRANSITION.shadow}
          />

          <AnimatePresence mode="wait">
            {showTabBarsContent ? (
              <motion.div
                key="toolbar"
                initial={{ opacity: 0, scale: 0.97 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 0, scale: 0.97 }}
                transition={{
                  opacity: {
                    duration: MORPH_TRANSITION.toolbarContentEnter.duration,
                    ease: MORPH_TRANSITION.toolbarContentEnter.ease,
                  },
                  scale: {
                    duration: MORPH_TRANSITION.toolbarContentEnter.duration,
                    ease: MORPH_TRANSITION.toolbarContentEnter.ease,
                  },
                }}
                className="flex items-center"
                style={{ gap: 6 }}
              >
                <TabBarsContent activeItem={activeItem} onItemClick={setActiveItem} onMenuClick={toggleCollapse} />
              </motion.div>
            ) : (
              <motion.div
                key="sidebar"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                transition={{
                  opacity: isCollapsed ? MORPH_TRANSITION.sidebarContentExit : MORPH_TRANSITION.sidebarContentEnter,
                }}
                className="w-full h-full"
              >
                <SidebarContent activeItem={activeItem} onItemClick={setActiveItem} onMenuClick={toggleCollapse} />
              </motion.div>
            )}
          </AnimatePresence>
        </motion.div>
      </motion.div>
    </>
  )
}

'use client';

import { SidebarFooter } from "@/components/sidebar/sidebar-footer";
import { SidebarHeader } from "@/components/sidebar/sidebar-header";
import { SidebarNav } from "@/components/sidebar/sidebar-nav";
import { NavigationItemId } from "@/lib/sidebar/navigation";

interface SidebarContentProps {
  activeItem: NavigationItemId;
  onItemClick: (id: NavigationItemId) => void;
  onMenuClick: () => void;
}

export function SidebarContent({ activeItem, onItemClick, onMenuClick }: SidebarContentProps) {
  return (
    <div className="box-border content-stretch flex flex-col items-start justify-between pl-0 pr-px py-0 relative size-full">
      <div className="content-stretch flex flex-col gap-[16px] items-start relative shrink-0 w-full">
        <SidebarHeader onMenuClick={onMenuClick} isCollapsed={false} />
        <SidebarNav activeItem={activeItem} onItemClick={onItemClick} isCollapsed={false} />
      </div>
      <div className="w-full">
        <SidebarFooter activeItem={activeItem} onItemClick={onItemClick} isCollapsed={false} />
      </div>
    </div>
  );
}

'use client';

import { motion } from "motion/react";

import { ThemeToggle } from "@/components/theme/theme-toggle";
import { useTheme } from "@/components/theme/theme-provider";
import { INTERACTION_ANIMATIONS, SPRING_CONFIG, TRANSITIONS } from "@/lib/sidebar/animations";
import { getColor } from "@/lib/sidebar/theme";
import svgPaths from "@/lib/sidebar/svg-paths";

function Logo() {
  const { theme } = useTheme();
  const isDark = theme === "dark";

  return (
    <motion.div
      className="content-stretch flex gap-[7.139px] items-center relative shrink-0"
      data-name="Logo"
      whileHover={INTERACTION_ANIMATIONS.scale.hover}
      transition={SPRING_CONFIG.bouncy}
    >
      <div className="h-[24px] overflow-clip relative shrink-0 w-[25.09px]" data-name="Logo Icon">
        <div className="absolute h-[24.421px] left-0 top-0 w-[25.09px]" data-name="Vector">
          <div className="absolute inset-0">
            <svg className="block size-full" fill="none" preserveAspectRatio="none" viewBox="0 0 26 25">
              <path
                clipRule="evenodd"
                d={svgPaths.p3a69b870}
                fill={getColor.indicator(isDark)}
                fillOpacity="0.85"
                fillRule="evenodd"
              />
            </svg>
          </div>
        </div>
      </div>
    </motion.div>
  );
}

function MenuButton({ onClick, size = "md" }: { onClick?: () => void; size?: "sm" | "md" }) {
  const { theme } = useTheme();
  const isDark = theme === "dark";
  const buttonSize = size === "sm" ? 24 : 36;
  const buttonRadius = size === "sm" ? 12 : 18;
  const iconSize = size === "sm" ? 12 : 16;

  return (
    <motion.div
      className="content-stretch flex items-center justify-center relative cursor-pointer"
      style={{ width: buttonSize, height: buttonSize, borderRadius: buttonRadius }}
      data-name="button"
      whileHover={{ backgroundColor: getColor.interactive.hover(isDark) }}
      whileTap={{ scale: 0.9 }}
      onClick={onClick}
      transition={TRANSITIONS.fast}
    >
      <div className="relative shrink-0" style={{ width: iconSize, height: iconSize }} data-name="icon">
        <svg className="block size-full" fill="none" preserveAspectRatio="none" viewBox="0 0 16 16">
          <path d={svgPaths.p19565b80} fill={getColor.indicator(isDark)} fillOpacity="0.85" />
        </svg>
      </div>
    </motion.div>
  );
}

interface SidebarHeaderProps {
  onMenuClick?: () => void;
  isCollapsed?: boolean;
}

export function SidebarHeader({ onMenuClick, isCollapsed = false }: SidebarHeaderProps) {
  return (
    <div className="relative shrink-0 w-full" data-name="SidebarHeader">
      <div className="flex flex-col items-center size-full">
        <div
          className={`box-border content-stretch flex flex-col items-center py-[12px] relative w-full ${
            isCollapsed ? "px-[12px]" : "pl-[16px] pr-[12px]"
          }`}
        >
          {isCollapsed ? (
            <div className="flex flex-col items-center gap-3 w-full">
              <Logo />
              <div className="flex items-center gap-2">
                <ThemeToggle size="sm" />
                <MenuButton onClick={onMenuClick} size="sm" />
              </div>
            </div>
          ) : (
            <div className="content-stretch flex h-[40px] items-start justify-between relative shrink-0 w-full" data-name="Container">
              <div className="basis-0 content-stretch flex grow items-center justify-between min-h-px min-w-px relative shrink-0" data-name="Container">
                <Logo />
                <div className="flex items-center gap-2">
                  <ThemeToggle />
                  <MenuButton onClick={onMenuClick} />
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

"use client"

import { motion } from "motion/react"
import { Moon, Sun } from "lucide-react"

import { IconButton } from "@/components/common/icon-button"
import { SidebarNavItem } from "@/components/sidebar/sidebar-nav-item"
import { UserProfile } from "@/components/sidebar/user-profile"
import { useTheme } from "@/components/theme/theme-provider"
import { containerVariantsDelayed, itemVariantsFromBottom } from "@/lib/sidebar/animations"
import { FOOTER_LINKS } from "@/lib/sidebar/navigation"
import type { NavigationProps } from "@/lib/sidebar/navigation"

interface SidebarFooterProps extends NavigationProps {
  isCollapsed?: boolean
}

export function SidebarFooter({ activeItem, onItemClick, isCollapsed = false }: SidebarFooterProps) {
  const { theme, toggleTheme } = useTheme()

  return (
    <motion.div
      className="content-stretch flex flex-col gap-[8px] items-start relative shrink-0 w-full"
      variants={containerVariantsDelayed}
      initial="hidden"
      animate="show"
    >
      {/* Documentation and Github nav items */}
      <motion.div variants={itemVariantsFromBottom} className="w-full px-[12px]">
        {FOOTER_LINKS.filter((link) => link.id === "docs" || link.id === "github").map((link) => (
          <SidebarNavItem
            key={link.id}
            icon={link.icon}
            label={link.label}
            isActive={activeItem === link.id}
            onClick={() => onItemClick?.(link.id)}
            isCollapsed={isCollapsed}
            variant="default"
          />
        ))}
      </motion.div>

      {/* Settings nav item (UserProfile component) */}
      <motion.div variants={itemVariantsFromBottom} className="w-full">
        <UserProfile isCollapsed={isCollapsed} />
      </motion.div>

      {/* Theme toggle button */}
      {!isCollapsed && (
        <motion.div variants={itemVariantsFromBottom} className="w-full px-[12px]">
          <div className="flex items-center justify-end gap-2">
            <IconButton
              icon={theme === "light" ? <Sun className="size-[18px]" /> : <Moon className="size-[18px]" />}
              label={theme === "light" ? "Light mode" : "Dark mode"}
              onClick={toggleTheme}
              data-testid="theme-toggle"
            />
          </div>
        </motion.div>
      )}
    </motion.div>
  )
}

"use client"

import { motion } from "motion/react"

import { containerVariants, itemVariants } from "@/lib/sidebar/animations"
import { MAIN_NAVIGATION } from "@/lib/sidebar/navigation"
import type { NavigationProps } from "@/lib/sidebar/navigation"
import { SidebarNavItem } from "@/components/sidebar/sidebar-nav-item"

interface SidebarNavProps extends NavigationProps {
  isCollapsed?: boolean
}

export function SidebarNav({ activeItem, onItemClick, isCollapsed = false }: SidebarNavProps) {
  return (
    <div className="relative shrink-0 w-full">
      <div className="overflow-auto size-full">
        <div
          className={`box-border content-stretch flex flex-col items-start py-0 relative w-full ${
            isCollapsed ? "px-[12px]" : "pl-[12px] pr-[16px]"
          }`}
        >
          <div className="content-stretch flex flex-col gap-[8px] items-start relative shrink-0 w-full">
            <motion.div
              className="content-stretch flex flex-col gap-[8px] items-start relative shrink-0 w-full"
              variants={containerVariants}
              initial="hidden"
              animate="show"
            >
              {MAIN_NAVIGATION.map((navItem) => (
                <motion.div key={navItem.id} variants={itemVariants} className="w-full">
                  <SidebarNavItem
                    icon={navItem.icon}
                    label={navItem.label}
                    variant={navItem.variant}
                    isActive={activeItem === navItem.id}
                    onClick={() => onItemClick?.(navItem.id)}
                    isCollapsed={isCollapsed}
                    layoutId={`nav-${navItem.id}`}
                  />
                </motion.div>
              ))}
            </motion.div>

            <div className="h-[112px] shrink-0 w-full" />
          </div>
        </div>
      </div>
    </div>
  )
}

'use client';

import { motion } from "motion/react";
import { ReactNode, memo } from "react";

import { useTheme } from "@/components/theme/theme-provider";
import { INTERACTION_ANIMATIONS, SPRING_CONFIG, TRANSITIONS } from "@/lib/sidebar/animations";
import { getColor } from "@/lib/sidebar/theme";
import { NavigationVariant } from "@/lib/sidebar/navigation";

interface SidebarNavItemProps {
  icon: ReactNode;
  label: string;
  isActive?: boolean;
  onClick?: () => void;
  variant?: NavigationVariant;
  isCollapsed?: boolean;
  layoutId?: string;
}

export const SidebarNavItem = memo(function SidebarNavItem({
  icon,
  label,
  isActive = false,
  onClick,
  variant = "default",
  isCollapsed = false,
  layoutId,
}: SidebarNavItemProps) {
  const isPrimary = variant === "primary";
  const { theme } = useTheme();
  const isDark = theme === "dark";
  const typographyClasses = isPrimary
    ? "font-['Inter_Variable:Medium',sans-serif] font-medium text-[12.906px]"
    : "font-['Inter_Variable:SemiBold',sans-serif] font-semibold text-[12.578px]";

  return (
    <motion.div
      className="h-[36px] relative rounded-[1000px] shrink-0 w-full cursor-pointer transition-colors duration-300"
      style={{ backgroundColor: isPrimary ? getColor.bg.container(isDark) : "transparent" }}
      whileHover={{ backgroundColor: getColor.bg.hover(isDark) }}
      whileTap={INTERACTION_ANIMATIONS.scaleSmall.tap}
      onClick={onClick}
      transition={TRANSITIONS.fast}
      data-testid={`nav-item-${label.toLowerCase().replace(/\s+/g, "-")}`}
      data-active={isActive}
      layoutId={layoutId}
      layout
    >
      <div className="content-stretch flex flex-row items-center w-full h-full">
        <motion.div
          className={`box-border content-stretch flex h-[36px] items-center py-0 relative w-full rounded-[1000px] transition-colors duration-300 ${
            isCollapsed ? "justify-center px-0" : "gap-[8px] px-[14px]"
          }`}
          initial={{ backgroundColor: "rgba(255, 255, 255, 0)" }}
          animate={{ backgroundColor: isActive ? getColor.bg.active(isDark) : "transparent" }}
          whileHover={{
            backgroundColor: isActive ? getColor.bg.activeHover(isDark) : getColor.bg.hover(isDark),
          }}
          transition={TRANSITIONS.fast}
        >
          <motion.div className="shrink-0" whileHover={INTERACTION_ANIMATIONS.scaleIcon.hover} transition={SPRING_CONFIG.bouncy}>
            {icon}
          </motion.div>
          {!isCollapsed && (
            <motion.div
              className={`flex flex-col ${typographyClasses} justify-center leading-[0] relative shrink-0 text-nowrap transition-colors duration-300`}
              style={{
                color: isActive
                  ? getColor.text.active(isDark)
                  : isPrimary
                  ? getColor.text.primary(isDark)
                  : getColor.text.secondary(isDark),
              }}
              initial={{ opacity: 1 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
            >
              <p className="leading-[14px] whitespace-pre">{label}</p>
            </motion.div>
          )}
        </motion.div>
      </div>
    </motion.div>
  );
});

"use client"

import { motion } from "motion/react"
import { useMemo } from "react"

import { useTheme } from "@/components/theme/theme-provider"
import { MenuButton } from "@/components/sidebar/menu-button"
import { SearchIconButton } from "@/components/sidebar/search-icon-button"
import { TabBarsTabItem } from "@/components/sidebar/tab-bars-tab-item"
import { UserAvatar } from "@/components/sidebar/user-avatar"
import { MAIN_NAVIGATION } from "@/lib/sidebar/navigation"
import type { NavigationItemId } from "@/lib/sidebar/navigation"

interface FloatingTabBarsProps {
  activeItem: NavigationItemId
  onItemClick: (id: NavigationItemId) => void
  onMenuClick: () => void
}

export function FloatingTabBars({ activeItem, onItemClick, onMenuClick }: FloatingTabBarsProps) {
  const { theme } = useTheme()
  const isDark = theme === "dark"

  const mainNavItems = useMemo(
    () => MAIN_NAVIGATION.filter((item) => item.id !== "settings" && item.id !== "search"),
    [],
  )

  const shadowStyle = useMemo(
    () => ({
      boxShadow: isDark
        ? "0 4px 24px rgba(0, 0, 0, 0.3), 0 0 1px rgba(255, 255, 255, 0.1)"
        : "0 4px 24px rgba(0, 0, 0, 0.06), 0 0 1px rgba(0, 0, 0, 0.04)",
    }),
    [isDark],
  )

  const hoverShadowStyle = useMemo(
    () => ({
      boxShadow: isDark
        ? "0 6px 32px rgba(0, 0, 0, 0.4), 0 0 1px rgba(255, 255, 255, 0.15)"
        : "0 6px 32px rgba(0, 0, 0, 0.08), 0 0 1px rgba(0, 0, 0, 0.06)",
    }),
    [isDark],
  )

  return (
    <div className="fixed z-50 top-3 left-1/2 -translate-x-1/2" role="toolbar" aria-label="Main navigation toolbar">
      <motion.div
        initial={{ opacity: 0, scale: 0.96, y: -8 }}
        animate={{ opacity: 1, scale: 1, y: 0 }}
        exit={{ opacity: 0, scale: 0.96, y: -8 }}
        transition={{ type: "spring", stiffness: 420, damping: 32, mass: 0.5 }}
      >
        <motion.div
          className="flex items-center backdrop-blur-[12px] backdrop-filter transition-colors duration-500"
          style={{
            height: 48,
            paddingLeft: 12,
            paddingRight: 12,
            paddingTop: 6,
            paddingBottom: 6,
            borderRadius: 24,
            gap: 6,
            backgroundColor: "rgba(255, 255, 255, 0.05)",
            ...shadowStyle,
            willChange: "transform, box-shadow",
          }}
          whileHover={{
            backgroundColor: "rgba(255, 255, 255, 0.08)",
            ...hoverShadowStyle,
          }}
          transition={{ duration: 0.15 }}
        >
          <MenuButton onClick={onMenuClick} />

          <nav className="flex items-center gap-1 rounded-[168px]" aria-label="Main navigation">
            {mainNavItems.map((item) => (
              <TabBarsTabItem
                key={item.id}
                label={item.label}
                isActive={activeItem === item.id}
                onClick={() => onItemClick(item.id)}
                layoutId={`nav-${item.id}`}
              />
            ))}
          </nav>

          <div className="flex-1 min-w-[8px]" aria-hidden="true" />

          <div className="flex items-center gap-2">
            <UserAvatar />
            <SearchIconButton onClick={() => onItemClick("search")} />
          </div>
        </motion.div>
      </motion.div>
    </div>
  )
}

