function getThemeColor(isDark: boolean, colorPath: string): string {
  const keys = colorPath.split(".");
  let value: any = THEME_COLORS;

  for (const key of keys) {
    value = value[key];
    if (value === undefined) return "";
  }

  return isDark ? value.dark : value.light;
}

