export function useNavigationState(initialItem: NavigationItemId = DEFAULT_ACTIVE_ITEM) {
  const [activeItem, setActiveItem] = useState<NavigationItemId>(initialItem);

  const handleItemClick = useCallback((id: NavigationItemId) => {
    setActiveItem(id);
  }, []);

  const resetToDefault = useCallback(() => {
    setActiveItem(DEFAULT_ACTIVE_ITEM);
  }, []);

  return {
    activeItem,
    setActiveItem: handleItemClick,
    resetToDefault,
  };
}

"use client"

import { useState, useEffect, useCallback, useRef } from "react"

export function useNavigationMorph(contentSwitchDelay = 300) {
  const [isCollapsed, setIsCollapsed] = useState(false)
  const [showTabBarsContent, setShowTabBarsContent] = useState(false)
  const timerRef = useRef<NodeJS.Timeout | null>(null)
  const isTransitioningRef = useRef(false)

  const toggleCollapse = useCallback(() => {
    if (isTransitioningRef.current) return
    isTransitioningRef.current = true
    setIsCollapsed((prev) => !prev)

    // Reset transition lock after animation completes
    setTimeout(() => {
      isTransitioningRef.current = false
    }, contentSwitchDelay + 100)
  }, [contentSwitchDelay])

  const collapse = useCallback(() => {
    if (!isCollapsed && !isTransitioningRef.current) {
      toggleCollapse()
    }
  }, [isCollapsed, toggleCollapse])

  const expand = useCallback(() => {
    if (isCollapsed && !isTransitioningRef.current) {
      toggleCollapse()
    }
  }, [isCollapsed, toggleCollapse])

  useEffect(() => {
    // Clear any existing timer
    if (timerRef.current) {
      clearTimeout(timerRef.current)
    }

    if (isCollapsed) {
      // Show toolbar content after delay
      timerRef.current = setTimeout(() => {
        setShowTabBarsContent(true)
      }, contentSwitchDelay)
    } else {
      // Hide toolbar content immediately when expanding
      setShowTabBarsContent(false)
    }

    return () => {
      if (timerRef.current) {
        clearTimeout(timerRef.current)
      }
    }
  }, [isCollapsed, contentSwitchDelay])

  return {
    isCollapsed,
    showTabBarsContent,
    toggleCollapse,
    collapse,
    expand,
  }
}

import { Transition } from "motion/react";

type TransitionWithEase = Extract<Transition, { ease?: unknown }>;

export function createStaggerDelay(index: number, baseDelay = 0, staggerAmount = 0.05): number {
  return baseDelay + index * staggerAmount;
}

export function createSpringTransition(stiffness = 300, damping = 30, mass = 0.8): Transition {
  return {
    type: "spring",
    stiffness,
    damping,
    mass,
  };
}

export function createEaseTransition(
  duration: number,
  ease: TransitionWithEase["ease"] = "easeInOut",
  delay = 0
): TransitionWithEase {
  return {
    duration,
    ease,
    delay,
  } satisfies TransitionWithEase;
}

export function createCombinedTransition(
  baseTransition: Transition,
  overrides: Record<string, unknown> = {}
): Transition & Record<string, unknown> {
  return {
    ...baseTransition,
    ...overrides,
  };
}

export function getShadow(isDark: boolean, isCollapsed: boolean): string {
  if (!isCollapsed) {
    return "0 0 0 rgba(0, 0, 0, 0), 0 0 0 rgba(0, 0, 0, 0)";
  }

  return isDark
    ? "0 4px 24px rgba(0, 0, 0, 0.3), 0 0 1px rgba(255, 255, 255, 0.1)"
    : "0 4px 24px rgba(0, 0, 0, 0.06), 0 0 1px rgba(0, 0, 0, 0.04)";
}

export function getBackgroundWithOpacity(r: number, g: number, b: number, opacity: number): string {
  return `rgba(${r}, ${g}, ${b}, ${opacity})`;
}

type StorageValue = string | number | boolean | object | null;

class SafeStorage {
  private isAvailable: boolean;

  constructor() {
    this.isAvailable = this.checkAvailability();
  }

  private checkAvailability(): boolean {
    try {
      const test = "__storage_test__";
      localStorage.setItem(test, test);
      localStorage.removeItem(test);
      return true;
    } catch (e) {
      return false;
    }
  }

  get<T = StorageValue>(key: string, defaultValue?: T): T | null {
    if (!this.isAvailable) {
      return defaultValue ?? null;
    }

    try {
      const item = localStorage.getItem(key);
      if (item === null) {
        return defaultValue ?? null;
      }
      return JSON.parse(item) as T;
    } catch (error) {
      console.warn(`Error reading from localStorage key "${key}":`, error);
      return defaultValue ?? null;
    }
  }

  set(key: string, value: StorageValue): boolean {
    if (!this.isAvailable) {
      return false;
    }

    try {
      localStorage.setItem(key, JSON.stringify(value));
      return true;
    } catch (error) {
      console.warn(`Error writing to localStorage key "${key}":`, error);
      return false;
    }
  }

  remove(key: string): boolean {
    if (!this.isAvailable) {
      return false;
    }

    try {
      localStorage.removeItem(key);
      return true;
    } catch (error) {
      console.warn(`Error removing from localStorage key "${key}":`, error);
      return false;
    }
  }

  clear(): boolean {
    if (!this.isAvailable) {
      return false;
    }

    try {
      localStorage.clear();
      return true;
    } catch (error) {
      console.warn("Error clearing localStorage:", error);
      return false;
    }
  }

  isStorageAvailable(): boolean {
    return this.isAvailable;
  }
}

